// src/components/About.js
import React from 'react';
import './About.css';

const About = () => {
  return (
    <div className="about-container">
      <h1>About Kala Kendra</h1>
      
      <div className="about-content">
        <h2>The Kalakar</h2>
        <p>
          Excited to introduce myself as Gunjan Choudhary, the Principal Designer at Kala Kendra! 
          Specializing in interior design, furniture design, and home decor, I adore turning spaces 
          into cozy, stylish havens. From detailed 3D models to the final charming touches, I'm here 
          to make your design dreams come true. Follow along for inspiration and a glimpse into my creative world!
        </p>
        
        <h2>About Kala Kendra</h2>
        <p>
          Kala Kendra is an innovative interior design firm creating stylish, functional spaces that 
          reflect clients' unique tastes. We transform every project into a personalized masterpiece.
        </p>
      </div>
    </div>
  );
};

export default About;
